    MAZE.object = new Object('images/atlas.jpg', MAZE.vertexPositions, MAZE.vertexNormals, MAZE.vertexTextureCoords, MAZE.vertexIndices, 1);
    BG = new Background(5 + 0.5 * Math.sqrt(MAZE.width * MAZE.width + MAZE.height * MAZE.height), MAZE.width * 0.5, MAZE.height * 0.5);
    BG.object = new Object("images/bg.jpg", BG.vertexPositions, BG.vertexNormals, BG.vertexTextureCoords, BG.vertexIndices, 2);
