function start() {
    if (!START) {
        START = true;
        document.getElementById('rules').style.display = 'none';
        audioPlay('audio');
        document.getElementById('audio').addEventListener('ended', function () {
            this.currentTime = 0;
        }, false);
        setAudio('audioEnd');
        setAudio('audioJump');
        setAudio('audioJump2');
        setAudio('audioReverse');
        TIMER2 = setInterval("TIME_COUNT++; TIMER_DOM.innerHTML=TIME_COUNT;", 1000);
    }
}

function setAudio(audioID) {
    document.getElementById(audioID).addEventListener('ended', function () {
        this.currentTime = 0;
        this.pause();
    }, false);
}

function audioPlay(audioID) {
    if (SOUND) {
        document.getElementById(audioID).play();
    }
}

function setSound() {
    if (SOUND) {
        SOUND = false;
        document.getElementById('hp').src = 'images/hpOff.png';
        document.getElementById('audio').pause();
    } else {
        SOUND = true;
        document.getElementById('hp').src = 'images/hpOn.png';
        audioPlay('audio');
    }
}

function win() {
    clearInterval(TIMER2);
    document.getElementById('score').innerHTML = TIME_COUNT + ' s';
    document.getElementById('win').style.display = 'block';
}

function savePartie() {
    var value = LEVEL + 'chuck' + TIME_COUNT;
    var exdays = 100;
    var exdate = new Date();
    exdate.setDate(exdate.getDate() + exdays);
    var c_value = escape(value) + ((exdays == null) ? "" : "; expires=" + exdate.toUTCString());
    document.cookie = "SpacegooMaze=" + c_value;
}

function getPartie() {
    var i, x, y, ARRcookies = document.cookie.split(";");
    for (i = 0; i < ARRcookies.length; i++) {
        x = ARRcookies[i].substr(0, ARRcookies[i].indexOf("="));
        y = ARRcookies[i].substr(ARRcookies[i].indexOf("=") + 1);
        x = x.replace(/^\s+|\s+$/g, "");
        if (x == "SpacegooMaze") {
            y = y.split('chuck');
            LEVEL = parseInt(y[0]);
            TIME_COUNT = parseInt(y[1]);
            return true;
        }
    }
    TIME_COUNT = 0;
    return false;
}

